import express from 'express';
import cors from 'cors';

export const serveDocs = (port: number, filename: string, dir: string) => {
  const app = express();
  return new Promise<void>((resolve, reject) => {
    app
      .listen(port, () => {
        console.log(`Served on port ${port}`);
        resolve();
      })
      .on('error', reject);
  });
};
